  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>cdr-grid</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>grid-2-example<span class="token punctuation">"</span></span> <span class="token special-attr"><span class="token attr-name">style</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span><span class="token value css language-css"><span class="token property">grid-template-areas</span><span class="token punctuation">:</span> <span class="token string">'logo header header header'</span> <span class="token string">'content content content sidebar'</span> <span class="token string">'footer footer footer footer'</span><span class="token punctuation">;</span></span><span class="token punctuation">"</span></span></span><span class="token punctuation">></span></span>
